$(document).ready(function () {
    $("img, a").on("dragstart", function (b) {
        b.preventDefault()
    });
    $('a[href*="#"]').bind("click", function (b) {
        var a = $(this);
        jQuery(".nav.navbar-nav").find("LI").removeClass("active");
        a.closest("LI").addClass("active");
        $("html, body").stop().animate({scrollTop: $(a.attr("href")).offset().top}, 1000);
        b.preventDefault()
    });
  
  	$('.front').equalHeights();
  
    /*$("#card1").flip({trigger: "manual"});
    $("#card2").flip({trigger: "manual"});
    $("#card3").flip({trigger: "manual"})*/
});
